import { useCallback, useRef, useEffect } from 'react';

// Subtle click sound as base64 - extremely short, muted system-like click
const CLICK_SOUND_BASE64 = 'data:audio/mp3;base64,';

interface HapticFeedbackOptions {
  enableSound?: boolean;
  intensity?: 'light' | 'medium' | 'heavy';
}

// Singleton audio context and buffer
let audioContext: AudioContext | null = null;
let clickBuffer: AudioBuffer | null = null;
let isAudioInitialized = false;

const initializeAudio = async () => {
  if (isAudioInitialized || typeof window === 'undefined') return;
  
  try {
    audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();
    
    // Decode the click sound
    const response = await fetch(CLICK_SOUND_BASE64);
    const arrayBuffer = await response.arrayBuffer();
    clickBuffer = await audioContext.decodeAudioData(arrayBuffer);
    isAudioInitialized = true;
  } catch (error) {
    // Silently fail - audio is optional
    console.debug('Audio initialization skipped');
  }
};

// Initialize on first user interaction
if (typeof window !== 'undefined') {
  const initOnInteraction = () => {
    initializeAudio();
    window.removeEventListener('touchstart', initOnInteraction);
    window.removeEventListener('click', initOnInteraction);
  };
  window.addEventListener('touchstart', initOnInteraction, { once: true });
  window.addEventListener('click', initOnInteraction, { once: true });
}

export const useHapticFeedback = (options: HapticFeedbackOptions = {}) => {
  const { enableSound = false, intensity = 'light' } = options;
  const lastTriggerRef = useRef<number>(0);

  // Debounce to prevent rapid triggers
  const DEBOUNCE_MS = 50;

  const playClickSound = useCallback(() => {
    if (!enableSound || !audioContext || !clickBuffer) return;
    
    try {
      const source = audioContext.createBufferSource();
      const gainNode = audioContext.createGain();
      
      source.buffer = clickBuffer;
      
      // Very subtle volume based on intensity
      const volumes = {
        light: 0.03,
        medium: 0.05,
        heavy: 0.08,
      };
      
      gainNode.gain.value = volumes[intensity];
      
      source.connect(gainNode);
      gainNode.connect(audioContext.destination);
      
      source.start(0);
    } catch (error) {
      // Silently fail
    }
  }, [enableSound, intensity]);

  const triggerHaptic = useCallback(() => {
    const now = Date.now();
    if (now - lastTriggerRef.current < DEBOUNCE_MS) return;
    lastTriggerRef.current = now;

    // Try native haptic feedback on supported devices
    if ('vibrate' in navigator) {
      const durations = {
        light: 5,
        medium: 10,
        heavy: 15,
      };
      navigator.vibrate(durations[intensity]);
    }

    // Play subtle click sound if enabled
    if (enableSound) {
      playClickSound();
    }
  }, [enableSound, intensity, playClickSound]);

  return { triggerHaptic };
};

// Spring configurations for consistent Apple-feel animations
export const appleSpring = {
  tap: {
    type: "spring" as const,
    stiffness: 400,
    damping: 25,
    mass: 0.8,
  },
  hover: {
    type: "spring" as const,
    stiffness: 300,
    damping: 20,
  },
  release: {
    type: "spring" as const,
    stiffness: 500,
    damping: 30,
  },
};

// Consistent scale values
export const appleScale = {
  button: 0.97,
  card: 0.985,
  icon: 0.9,
  nav: 0.92,
  subtle: 0.98,
};

export default useHapticFeedback;